    private void btnChangePasswordActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnChangePasswordActionPerformed
        String currentPassword = new String(txtCurrentPassword.getPassword());
        String newPassword = new String(txtNewPassword.getPassword());
        String repeatPassword = new String(txtRepeatPassword.getPassword());

        if (currentPassword.isEmpty() || newPassword.isEmpty() || repeatPassword.isEmpty()) {
            JOptionPane.showMessageDialog(this, "Моля попълнете всички полета.");
            return;
        }

        if (!newPassword.equals(repeatPassword)) {
            JOptionPane.showMessageDialog(this, "Новата парола не съвпада с повторената парола.");
            return;
        }

        boolean success = q.changeUserPassword(user_id, currentPassword, newPassword);

        if (success) {
            JOptionPane.showMessageDialog(this, "Паролата беше успешно променена.");
            txtCurrentPassword.setText("");
            txtNewPassword.setText("");
            txtRepeatPassword.setText("");
        } else {
            JOptionPane.showMessageDialog(this, "Грешка: Текущата парола е неправилна или неуспешна операция.");
        }
    }//GEN-LAST:event_btnChangePasswordActionPerformed


